'use strict';
import React, {
  AppRegistry,
  Component,
  StyleSheet,
  Text,
  TextInput,
  View,
  Navigator,
  Image,
  AsyncStorage,
  TabBarIOS
} from 'react-native';
var Login = require('../components/login');
var TaskList = require('../components/taskList/taskList.android');
var Constants = require('../constants')
var Icon = require('react-native-vector-icons/FontAwesome');
var moment = require('moment')
// var TabNavigator = require('react-native-tab-navigator').default;
import TabNavigator from 'react-native-tab-navigator';
var Balance = require('../components/balance/balance.android');
var History = require('../components/history/history.android');
var Setting = require('../components/setting/setting.android');
import { connect } from 'react-redux';
var store = require('../components/store')

var _navigator;
// class delivermanApp extends Component {
module.exports = React.createClass({
  componentDidMount: function () {
    var _this = this
    AsyncStorage.getItem(Constants.STORAGE_USER_KEY).then(function (res) {
      _this.setState({
        user: JSON.parse(res)
      })
    }).done();
    store.subscribe(function () {
      if (store.getState().user === null) {
        try {
          _this.setState({
            user: null
          })
        } catch(e) {}
      }
    });
  },
  getInitialState: function (){
    return {
      user: null,
      loaded: false,
      selectedTab: 'tasks'
    }
  },
  render: function() {
    let _this = this;
    if (!_this.state.user) {
      return _this.renderLoginForm();
    } else {
      return (
        <TabBarIOS>
          <TabBarIOS.Item
            title="待送订单"
            icon={{uri: 'data:image/png;base64,', scale: 3}}
            selected={this.state.selectedTab === 'tasks'}
            onPress={() => {
              this.setState({
                selectedTab: 'tasks',
              });
            }}>
            <TaskList token={this.state.user.token}></TaskList>
          </TabBarIOS.Item>

          <TabBarIOS.Item
            title="配送历史"
            icon={{uri: 'data:image/png;base64,', scale: 3}}
            selected={this.state.selectedTab === 'history'}
            onPress={() => {
              this.setState({
                selectedTab: 'history',
              });
            }}>
            <History token={this.state.user.token}/>
          </TabBarIOS.Item>

          <TabBarIOS.Item
            title="对账"
            icon={{uri: 'data:image/png;base64,', scale: 3}}
            selected={this.state.selectedTab === 'balance'}
            onPress={() => {
              this.setState({
                selectedTab: 'balance',
              });
            }}>
            <Balance date={moment().format('YYYY-MM-DD')} token={_this.state.user.token}></Balance>
          </TabBarIOS.Item>

          <TabBarIOS.Item
            title="设置"
            icon={{uri: 'data:image/png;base64,', scale: 3}}
            selected={this.state.selectedTab === 'account'}
            onPress={() => {
              this.setState({
                selectedTab: 'account',
              });
            }}>
            <Setting navigator={_this.refs.navigator}/>
          </TabBarIOS.Item>
        </TabBarIOS>
      )
    }
  },

  renderLoginForm: function () {
    return (
      <Login onUserLogin={this.onUserLogin}></Login>
    )
  },

  onUserLogin: function(user) {
    // AlertIOS.alert('用户', user.name);
    console.log('login', user.name);
    this.setState({
      user: user,
      token: user.token,
      selectedTab: 'tasks'
    })
  }
});

var styles = StyleSheet.create({
  container: {
    // flex: 1
  },
  navigationBar: {backgroundColor: '#F00', height: 20}
});
